function changAllVert2() {
    var inject = function () {
      //headless
      const rttPrototype = Object.getOwnPropertyDescriptor(NetworkInformation.prototype, 'rtt');
      Object.defineProperty(NetworkInformation.prototype, 'rtt', {
          ...rttPrototype,
          get: function () {
              return 100
          },
      });
      // in case of broken image return random height/width
      ['height', 'width'].forEach(property => {
          const imageDescriptor = Object.getOwnPropertyDescriptor(HTMLImageElement.prototype, property);
          Object.defineProperty(HTMLImageElement.prototype, property, {
              imageDescriptor,
              get: function () {
                  if (this.complete && this.naturalHeight == 0) {
                      return 16;
                  }
                  return imageDescriptor.get.apply(this);
              },
          });
      }); 
      // hairline feature (headless can't render it normally)
      const elementDescriptor = Object.getOwnPropertyDescriptor(HTMLElement.prototype, 'offsetHeight');
      Object.defineProperty(HTMLElement.prototype, 'offsetHeight', {
          ...elementDescriptor,
          get: function () {
              if (this.id == 'modernizr') {
                  return 1;
              }
              return elementDescriptor.get.apply(this);
          },
      });
    } 
    inject()
}
changAllVert2()